import React from 'react';
import CartItem from './CartItem';

function Cart({ items, onRemove }) {
  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Your Cart</h2>
      <div className="space-y-4">
        {items.map(item => (
          <CartItem key={item.id} item={item} onRemove={onRemove} />
        ))}
      </div>
      <div className="mt-8 p-4 bg-gray-100 rounded-md">
        <h3 className="text-xl font-semibold mb-4">Summary</h3>
        <p className="text-lg">Total Items: {items.length}</p>
        <p className="text-lg">Total Amount: ${items.reduce((total, item) => total + item.price, 0).toFixed(2)}</p>
        <button className="mt-4 px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition duration-300">
          Checkout Now
        </button>
      </div>
    </div>
  );
}

export default Cart;
