import React from 'react';
import CartItem from './CartItem';

function Cart({ items, onRemove }) {
  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Your Cart</h2>
      <div className="space-y-4 mb-8">
        {items.map((item, index) => (
          <CartItem key={item.id} item={item} itemIndex={index} />
        ))}
      </div>
      <div className="mt-8 p-6 bg-gray-100 rounded-lg shadow-md">
        <h3 className="text-xl font-semibold mb-4">Summary</h3>
        <div className="space-y-2 text-lg">
          <p className="flex justify-between"><span>Total Items:</span> <span>{items.length}</span></p>
          <p className="flex justify-between"><span>Total Amount:</span> <span>${items.reduce((total, item) => total + item.price, 0).toFixed(2)}</span></p>
        </div>
        <button className="mt-4 w-full px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition duration-300">
          Checkout Now
        </button>
      </div>
    </div>
  );
}

export default Cart;
